import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import { gamesData } from './data/games';

function App() {
  const [games, setGames] = useState(gamesData);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const addToCart = (game) => {
    const existingItem = cart.find(item => item.id === game.id);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === game.id ? {...item, quantity: item.quantity + 1} : item
      ));
    } else {
      setCart([...cart, {...game, quantity: 1}]);
    }
  };

  const removeFromCart = (gameId) => {
    setCart(cart.filter(item => item.id !== gameId));
  };

  const updateQuantity = (gameId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(gameId);
      return;
    }
    setCart(cart.map(item => 
      item.id === gameId ? {...item, quantity} : item
    ));
  };

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  return (
    <div className="App">
      <Header cartItems={cart.length} toggleCart={toggleCart} />
      <main className="container">
        {showCart ? (
          <Cart 
            cart={cart} 
            removeFromCart={removeFromCart} 
            updateQuantity={updateQuantity}
            toggleCart={toggleCart}
          />
        ) : (
          <ProductList games={games} addToCart={addToCart} />
        )}
      </main>
    </div>
  );
}

export default App;
